 $(document).ready(function () {

                        var allowedDates = [];
                        
                            allowedDates.push('');
                        
                        flatpickr("#datepicker", {
                        inline: true, // Always visible
                        enable:allowedDates,
                        dateFormat: 'm/d/Y', // Date format
                        minDate: 'today', // Minimum date is today


                        });
                        $("#datepicker").change(function () {
                        var date= $(this).val();
                        fetchData(date);
                        });

                        function fetchData(selectedDate) {
                        $.ajax({
                        url: '/available-time-slots/',
                        type: 'POST',
                        data: { date: selectedDate, doc_id:  },
                        dataType: 'json',
                        success: function (data) {

                        sendDataToSelectMenu(data);
                        },
                        error: function (error) {

                        console.error('Error fetching data:', error);
                        }
                        });

                        }

                        function checkFields() {
                        setTimeout(function () {
                        var currentSlotTime = $('#slot_time').val().trim();

                        if (datePicker.val().trim() !== '' && currentSlotTime !== 'null') {
                        bookNowButton.removeAttr('disabled');
                        } else {
                        bookNowButton.attr('disabled', 'disabled');
                        }
                        }, 100);
                        }

                        var datePicker = $('#datepicker');
                        var bookNowButton = $('.btn.action-btn');
                        checkFields();

                        datePicker.change(checkFields);



                        });

