                    window.onload = function () {

                    const
                    gp=document.getElementById("gp");imagePreview=document.getElementById("imagePreview"),gp.addEventListener("change",function(){let
                    e=this.files[0];if(e){if(["png","jpg","jpeg"].includes(e.name.split(".").pop().toLowerCase())){let
                    t=new
                    FileReader;t.addEventListener("load",function(){imagePreview.src=t.result,imagePreview.style.display="block"}),t.readAsDataURL(e)}else
                    this.value="";imagePreview.src="",imagePreview.style.display="none"}});

                    flatpickr("#datepicker", {
                    dateFormat: 'm/d/Y',

                    });
                    };

                    checkForValidation=()=>{
                    var noerror=true
                    var dob=document.querySelector("#datepicker");
                    console.log(dob.value)
                    if( dob.value===''){
                    document.querySelector(".error_dob").style.display="inline";
                    noerror=false
